sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    // Close notes and Close code must be on the form
    if (!g_form.hasField('close_notes') || !g_form.hasField('u_close_type'))
        return;

    if (newValue != '4') {
        g_form.setMandatory('close_notes', false);
		g_form.setMandatory('u_close_type', false);
    }
    if (newValue == 4) {
        //g_form.setValue('incident_state', state);
        //g_form.setValue('state', state);
        g_form.setDisplay('close_notes', true);
        g_form.setMandatory('close_notes', true);
		g_form.setDisplay('u_close_type', true);
        g_form.setMandatory('u_close_type', true);
		//return;
    }
}
